<?	class ErrorController extends PageController implements IPageController	{        private $_smarty;                public function initializePages()        {            $this->_smarty=new SmartyExst();            $this->_smarty->assign("ErrorURL",urldecode($this->_parameters[1]));            $this->_smarty->assign("ModuleID",$this->_moduleID);            $this->_smarty->assign("ModuleName",$this->_moduleName);         }                public function __destruct()        {            $this->_smarty->display("errorTemplate.tpl");        }	}?>